import React, { Component } from "react";
import { Link } from "react-router-dom";
import "../config/fire";
import * as firebase from "firebase";
// import Studentprofile from '../components/Studentprofile';



firebase
  .database()
  .ref("studentssignup/")
  .on("value", snapshot => {
    snapshot.forEach(function (childSnapshot) {
      //   var key = childSnapshot.key;


      var childData = childSnapshot.val();
       new Student(
        childData

      )
      // printing eamil and password on console
      // console.log(childData.email);




      // if (childData.email === this.state.email && childData.password === this.state.password) {
      //   console.log("you are loged in")

      // }
      // else {
      //   console.log("password is incorrect")
      // }





    });
    // this.setState(state);

  });

export default class Student extends Component {
  constructor(props) {
    super(props);

     console.log("hellllll",props && props.email && props.email);
     console.log("hellllll",props.password);


     const dbe=this.props.email;
     const dbp=this.props.password;

    this.state = {
      lemail: "",
      lpassword: "",
      dbemail:dbe,
      dbpassword:this.props.password,
    };
  }


  loginCheck = (e) => {

    // console.log("lemail",this.state.lemail)
    // console.log("lpassword",this.state.lpassword)
    // console.log("dbemail",this.state.dbemail)
    // console.log("dbpassword",this.state.dbpassword)

    e.preventDefault();

    // console.log(fire.snapshot);


    // console.log("Functioncalled");
  };




  render() {
    // var emailchk = this.state.email;
    // var passwordchk = this.state.password;
    return (
      <div>


        <div className="cmd">
          <label>
            <h2>student log</h2>
          </label>
          {/* //pass variables of email and password and update the value of those variables when the value in textfield changes*/}
          {/* <form onSubmit={(e) => this.loginCheck(e, emailchk, passwordchk)}> */}
          <form onSubmit={this.loginCheck}>

            <input
              type="text"
              name="username"
              placeholder="email"
              required
              onChange={e => this.setState({ lemail: e.target.value })}
            />

            <br />

            <input
              type="password"
              name="password"
              placeholder="Password"
              required
              onChange={e => this.setState({ lpassword: e.target.value })}
            />

            <br />

            <input type="submit" value="Login"></input>
          </form>
        </div>

        {/*just for testing  */}
        <div>
          <Link to="/student/studentprofile" className="sbtn">
            <button className="sbtn">student profile</button>
          </Link>
        </div>
      </div>
    );
  }
}
