import React, { Component } from 'react'
import {Link} from "react-router-dom"
import "./config/fire"
import* as firbase from "firebase"

export default class Student extends Component {
    constructor(props) {
        super(props)

        this.state = {
            username: "",
            password: ""
        };
    };

    handlechange = (e) => {
        this.setState({
            username: e.target.value,
            password: e.target.value
        })
    }

    handlesubmit = (e) => {
        e.preventDefault()
        console.log(this.state)
    }

    componentDidMount () {
        firebase.database().ref("students").set({
        name:"dawood",
        age:21
        })
      }

    render() {
        return (

            <div>
                {/* <img src={Imga} width="100%" height="50%"/> */}

                    <div className="cmd">

                        <label><h2>Student login</h2></label>
                        <form onSubmit={this.handlesubmit}>
                            <input type="text" id="username" placeholder="Username" required onChange={this.handlechange} /><br />

                            <input type="password" id="password" name="password" placeholder="Password" required onChange={this.handlechange} /><br />

                            <input type="submit" value="Login"></input>
                        </form>

                    </div>

                   {/*just for testing  */}
             <div>
                 <Link to="/student/studentprofile" className="sbtn">
                     <button className="sbtn">student profile</button>
                 </Link>
             </div>

            </div>

        )
    }
}
