import React, { Component } from 'react'




// import { Route } from "react-router-dom"
// import Imga from "../images/1.jpg"
import { Link } from "react-router-dom"
import { throwStatement } from '@babel/types';



export default class Studentpprofile extends Component {
    constructor(props) {
        super(props)

        this.state = {
            visible: false,
            name: "",
            namelist: [],
            age: "",
            agelist: [],
            uni: "",
            unilist: [],
            college: "",
            collegelist: [],
            subjects: "",
            subjectslist: []

        };
    };
    handlesubmit = (e) => {
        e.preventDefault();
        let name1 = [...this.state.namelist];
        name1.push({
            name: this.state.name
        });

        let age1 = [...this.state.agelist];
        age1.push({
            age: this.state.age
        });

        let uni1 = [...this.state.unilist];
        uni1.push({
            uni: this.state.uni
        });

        let college1 = [...this.state.collegelist];
        college1.push({
            college: this.state.college
        });

        let subject1 = [...this.state.subjectslist];
        subject1.push({
            subjects: this.state.subjects
        });



        this.setState({

            visible: true,
            namelist,
            name: '',

            agelist,
            age: '',

            unilist,
            uni: '',

            collegelist,
            college: '',

            subjectslist,
            subjects: '',

        });

    };



handlechange = (e) => {
    this.setState({

        name: e.target.value,
        age: e.target.value,
        uni: e.target.value,
        college: e.target.value,
        subjects: e.target.value
    });


}

render() {
    return (
        <div>
            <div>
                <div className="studentprofile">
                    <br />
                    <div className="stddiv">

                        <label><h2>students details</h2></label>
                        <form onSubmit={this.handlesubmit.bind(this)}>
                            <input type="text"
                                id="name"
                                name="name"
                                placeholder="fullname"
                                value={this.state.name}
                                onChange={this.handlechange.bind(this)}
                                required />
                            <br />

                            <input
                                type="text"
                                name="age"
                                id="age"
                                required
                                placeholder="age"
                                value={this.state.age}
                                onChange={this.handlechange.bind(this)} />
                            <br />

                            <input type="text"
                                id="uni"
                                name="uni"
                                placeholder="university"
                                value={this.state.uni}
                                required
                                onChange={this.handlechange.bind(this)} />
                            <br />

                            <input type="text"
                                id="college"
                                name="college"
                                placeholder="college"
                                value={this.state.college}
                                required
                                onChange={this.handlechange.bind(this)} />
                            <br />

                            <input type="text"
                                name="subjects"
                                id="subjects"
                                placeholder="subjects"
                                required
                                value={this.state.subjects}
                                onChange={this.handlechange.bind(this)} />
                            <br />
                            <input type="submit" value="Login" />
                        </form>

                    </div>


                    <div>
                        <br />
                        <br />
                        <br />
                        <br />
                        <Link to="/company/allcompanydata">

                            <button className="click">companies data</button>
                        </Link>
                        <br />
                        <br />
                        <Link to="/student/allstudentdata">

                            <button className="click">student data</button>
                        </Link>

                    </div>

                </div>
                {this.state.visible ? <div className="datashow">
                    <div className="datashowb">
                        <br />
                        <p className="p">name: {this.state.name}</p>
                        <p className="p">age: {this.state.age}</p>
                        <p className="p">uni: {this.state.uni}</p>
                        <p className="p">college: {this.state.college}</p>
                        <p className="p">subjects: {this.state.subjects}</p>
                        <br />
                    </div>
                </div> : null}


            </div>
            {/* <div className="datashow">
                   <p>{this.state.name}</p>
                    <p>{this.state.age}</p>
                    <p>{this.state.uni}</p>
                    <p>{this.state.college}</p>
                    <p>{this.state.subjects}</p>
                   </div>  */}


        </div>
    )
}
}
