import React, { Component } from "react";
import { Link } from "react-router-dom";
import "../config/fire";
import * as firebase from "firebase";
import Studentprofile from '../components/Studentprofile';

export default class Student extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: ""
    };
  }

  loginCheck = (e, emailchk, passwordchk) => {
    this.setState({
      email:e.target.v
    })
    e.preventDefault();
    firebase
      .database()
      .ref("studentssignup/")
      .on("value", snapshot => {
        snapshot.forEach(function (childSnapshot) {
          //   var key = childSnapshot.key;
          var childData = childSnapshot.val();

          // printing eamil and password on console
          // console.log(childData.email);
          // console.log(childData.password);


          if (childData.email == emailchk && childData.password == passwordchk) {
            console.log("you are loged in")

          }
          else {
              console.log("password is incorrect")
              }
              
          

          console.log(emailchk);
          console.log(passwordchk);

        });
        // this.setState(state);

      });

    // console.log("Functioncalled");
  };

  render() {
    
    return (
      <div>


        <div className="cmd">
          <label>
            <h2>student log</h2>
          </label>
          {/* //pass variables of email and password and update the value of those variables when the value in textfield changes*/}
          <form onSubmit={(e) => this.loginCheck}>
            <input
              type="text"
              name="username"
              placeholder="email"
              required
              onChange={e => this.setState({ email: e.target.value })}
            />
            <br />

            <input
              type="password"
              name="password"
              placeholder="Password"
              required
              onChange={e => this.setState({ password: e.target.value })}
            />
            <br />

            <input type="submit" value="Login"></input>
          </form>
        </div>

        {/*just for testing  */}
        <div>
          <Link to="/student/studentprofile" className="sbtn">
            <button className="sbtn">student profile</button>
          </Link>
        </div>
      </div>
    );
  }
}
